import { BsHeartFill, BsHeart, BsThreeDots } from "react-icons/bs";
import { BiCommentDetail } from "react-icons/bi";
import useUser from "../../hooks/useUser";
import "./PostCSS.css";
import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";

const Post = ({ id, userName, img, comments, likes, description, canLikeOrComment }) => {
  const { user, isLoading } = useUser();
  const [canLike, setCanLike] = useState(false)

  useEffect(() => {
    const canLikeFunc = async () => {
      console.log(user);
      const token = user && (await user.getIdToken());
      // console.log(token);
      const header = token ? { authtoken: token } : {}
      const response = await axios.get(`/api/posts/canlike/${id}`, header)
      // console.log(response); 
    }

    if (!isLoading) {
      canLikeFunc()
    }

  }, [])

  return (
    <div className="post">
      <div className="header-section">
        {userName} <BsThreeDots />{" "}
      </div>
      <div className="img-section">
        <img src={img} alt="Post Img" />
      </div>
      <div className="like-comment-description-section">
        <div className="like-comment">
          <BsHeart />
          <BiCommentDetail />
        </div>
        <div className="like-counter">{likes} likes</div>
        <div className="post-description">
          <span className="post-id">{userName}</span> <span>{description}</span>
        </div>
        <div className="comment-section">
          View all {comments.length} comments
        </div>
      </div>
    </div>
  );
};

export default Post;
