import { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import useUser from "../../hooks/useUser";
import axios from "axios";
import { BsHeartFill, BsHeart, BsThreeDots } from "react-icons/bs";
import { BiCommentDetail } from "react-icons/bi";
import "./PostCSS.css";
import { useEffect } from "react";

const Post = ({ postInfo}) => {
  const [liked, setLiked] = useState(false);
  const { user, isLoading } = useUser();
  const navigate = useNavigate();

  useEffect(() => {
    getPostInfo();
  }, []);

  useEffect(() => {
    handleLike();
  }, [liked]);

  const getPostInfo = useCallback(() => {
    const getInfo = async () => {
      const token = user && (await user.getIdToken());
      const headers = token ? { authtoken: token } : {};
      const response = await axios.get(`/api/posts/liked/${postInfo.id}`, null, {
        headers,
      });

      setLiked(response.liked);
    };

    if (!isLoading) {
      getInfo();
    } else {
      return false;
    }
  }, [user, isLoading]);

  const handleLike = useCallback(
    (id) => {
      const likePost = async () => {
        const token = user && (await user.getIdToken());
        const headers = token ? { authtoken: token } : {};
        const response = await axios.put(`/api/posts/like-unlike/${postInfo.id}`, null, {
          headers,
        });

        setLiked(response.liked);
      };

      if (!isLoading) {
        likePost();
      } else {
        return navigate("/login");
      }
    },
    [user, isLoading]
  );

  return (
    <div className="post" key={postInfo._id}>
      <div className="header-section">
        {postInfo.user} <BsThreeDots />{" "}
      </div>
      <div className="img-section">
        <img src={postInfo.imgURL} alt="Post Img" />
      </div>
      <div className="like-comment-description-section">
        <div className="like-comment">
          {liked ? (
            <BsHeart onClick={() => setLiked(true)} />
          ) : (
            <BsHeartFill
              onClick={() => setLiked(false)}
              style={{ color: "rgb(245, 65, 65)" }}
            />
          )}
          <BiCommentDetail />
        </div>
        <div className="like-counter">{postInfo.likedby.length} likes</div>
        <div className="post-description">
          <span className="post-id">{postInfo.user}</span>{" "}
          <span>{post.description}</span>
        </div>
        <div className="comment-section">
          View all {postInfo.comments.length} comments
        </div>
      </div>
    </div>
  );
};

export default Post;
