import { useState } from "react";
import { useRef } from "react";
import "./MenuCSS.css";

const Menu = ({ setMenu }) => {
  const menu = useRef();
  const [edit, setEdit] = useState(false);
  const [editValue, setEditValue] = useState("");
  const [deletePost, setDeletePost] = useState(false);

  if (edit) {
    return (
      <div
        className="menu-container"
        ref={menu}
        onClick={(e) => {
          if (e.target === menu.current) {
            setMenu(false);
          }
        }}
      >

        <menu className="horizontal">
        What would you like to change the description too?
          <div className="menu-item">Edit Post</div>
          <div className="menu-item red">Delete Post</div>
        </menu>
      </div>
    );
  }

  return (
    <div
      className="menu-container"
      ref={menu}
      onClick={(e) => {
        if (e.target === menu.current) {
          setMenu(false);
        }
      }}
    >
      <menu>
        <div className="menu-item" onClick={() => setEdit(true)}>Edit Post</div>
        <div className="menu-item red" onClick={() => setDeletePost(true)}>Delete Post</div>
      </menu>
    </div>
  );
};

export default Menu;
