import { useState } from "react";
import { useRef } from "react";
import "./MenuCSS.css";

const Menu = ({ setMenu }) => {
  const menu = useRef();
  const [edit, setEdit] = useState(false);
  const [editValue, setEditValue] = useState("");
  const [deletePost, setDeletePost] = useState(false);

  if (edit) {
    return (
      <div
        className="menu-container horizontal"
        ref={menu}
        onClick={(e) => {
          if (e.target === menu.current) {
            setMenu(false);
          }
        }}
      >
        <menu>
          <div className="menu-title">
            What would you like to change the description too?
          </div>
          <textarea name="" id="" cols="30"></textarea>
          <div className="horizontal">
            <div className="menu-item red">Cancel</div>
            <div className="menu-item">Edit</div>
          </div>
        </menu>
      </div>
    );
  }

  if (deletePost) {
    return (
      <div
        className="menu-container horizontal"
        ref={menu}
        onClick={(e) => {
          if (e.target === menu.current) {
            setMenu(false);
          }
        }}
      >
        <menu>
          <div className="menu-title">
            What would you like to change the description too?
          </div>
          <textarea name="" id="" cols="30"></textarea>
          <div className="horizontal">
            <div className="menu-item red">Cancel</div>
            <div className="menu-item">Edit</div>
          </div>
        </menu>
      </div>
    );
  }

  return (
    <div
      className="menu-container"
      ref={menu}
      onClick={(e) => {
        if (e.target === menu.current) {
          setMenu(false);
        }
      }}
    >
      <menu>
        <div className="menu-item" onClick={() => setEdit(true)}>
          Edit Post
        </div>
        <div className="menu-item red" onClick={() => setDeletePost(true)}>
          Delete Post
        </div>
      </menu>
    </div>
  );
};

export default Menu;
