import { BsHeartFill, BsHeart, BsThreeDots } from "react-icons/bs";
import { useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";

const LikeAndComment = ({ postId, liked, likedby, user }) => {
  const [likes, setLikes] = useState(likedby);
  const navigate = useNavigate();

  const handleLike = useCallback(async () => {
    if (user === null) {
      return navigate("/login");
    }
    const token = user && (await user.getIdToken());
    const headers = token ? { authtoken: token } : {};
    const response = await axios.put(
      `/api/posts/likeUnlike/${postInfo._id}`,
      null,
      {
        headers,
      }
    );
    setLikes(response.data.length);
    liked.setLiked(!liked);
  }, [user, liked, postId, navigate]);

  return (
    <>
      <div className="like-comment">
        {!liked ? (
          <BsHeart onClick={handleLike} />
        ) : (
          <BsHeartFill
            onClick={handleLike}
            style={{ color: "rgb(245, 65, 65)" }}
          />
        )}
        <BiCommentDetail />
      </div>
      <div className="like-counter">{likes} likes</div>
    </>
  );
};

export default LikeAndComment;
