import { useState, useEffect, useCallback } from "react";
import useUser from "../../hooks/useUser";
import axios from "axios";
import { BsHeartFill, BsHeart, BsThreeDots } from "react-icons/bs";
import { BiCommentDetail } from "react-icons/bi";
import "./PostCSS.css";

const Post = () => {
  const [postData, setPostData] = useState([]);
  const { user, isLoading } = useUser();

  const getPostInfo = useCallback((id) => {
    const getInfo = async () => {
      const token = user && (await user.getIdToken());
      const headers = token ? { authtoken: token } : {};
      const response = await axios.put(`/api/posts/liked/${id}`, null, {
        headers,
      });
      console.log(response);
    };

    if (!isLoading) {
      getInfo();
    } else {
      return false
    }
  }, [user, isLoading]);

  useEffect(() => {
    const getData = async () => {
      const response = await axios.get("/api/posts");
      setPostData(response.data);
    };

    getData();

    // const getPostInfo = async () => {
    //   const token = user && (await user.getIdToken());
    //   const headers = token ? { authtoken: token } : {};
    //   const response = await axios.put(`/api/posts/${id}/like-unlike`, null, {
    //     headers,
    //   });
    //   console.log(response);
    // };

    // if (!isLoading) {
    //   canLikeFunc();
    // }
  }, []);

  return (
    <>
      {postData.map((post) => {
        return (
          <div className="post">
            <div className="header-section">
              {post.user} <BsThreeDots />{" "}
            </div>
            <div className="img-section">
              <img src={post.imgURL} alt="Post Img" />
            </div>
            <div className="like-comment-description-section">
              <div className="like-comment">
                {getPostInfo() ? <BsHeart /> : BsHeartFill}
                <BiCommentDetail />
              </div>
              <div className="like-counter">{post.likedby.length} likes</div>
              <div className="post-description">
                <span className="post-id">{post.user}</span>{" "}
                <span>{post.description}</span>
              </div>
              <div className="comment-section">
                View all {post.comments.length} comments
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
};

export default Post;
