import { useState, useCallback, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import useUser from "../../hooks/useUser";
import axios from "axios";
import Menu from "../Menu/Menu";
import { BsHeartFill, BsHeart, BsThreeDots } from "react-icons/bs";
import { BiCommentDetail } from "react-icons/bi";
import "./PostCSS.css";

const Post = ({ postInfo, container }) => {
  const [liked, setLiked] = useState(false);
  const [likes, setLikes] = useState(postInfo.likedby.length);
  const { user, isLoading } = useUser();
  const [menu, setMenu] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const getPostInfo = async () => {
      const token = user && (await user.getIdToken());
      const headers = token ? { authtoken: token } : {};
      const response = await axios.get(`/api/posts/liked/${postInfo._id}`, {
        headers,
      });

      setLiked(response.data.liked);
    };

    if (!isLoading) {
      getPostInfo();
    }
  }, [isLoading, user, postInfo._id]);

  const handleLike = useCallback(async () => {
    if (user === null) {
      return navigate("/login");
    }
    const token = user && (await user.getIdToken());
    const headers = token ? { authtoken: token } : {};
    const response = await axios.put(
      `/api/posts/like-unlike/${postInfo._id}`,
      null,
      {
        headers,
      }
    );
    setLikes(response.data.length);
    setLiked(!liked);
  }, [user, liked, postInfo._id, navigate]);

  return (
    <>
      {menu && <Menu setMenu={setMenu} container={container}/>}
      <div className="post">
        <div className="header-section">
          {postInfo.user}{" "}
          <BsThreeDots
            onClick={() => {
              setMenu(true);
              container.current.classList.toggle("open")
              container.current.style.top(`-${window.scrollY}px`)
            }}
          />{" "}
        </div>
        <div className="img-section">
          <img src={postInfo.imgURL} alt="Post Img" />
        </div>
        <div className="like-comment-description-section">
          <div className="like-comment">
            {!liked ? (
              <BsHeart onClick={handleLike} />
            ) : (
              <BsHeartFill
                onClick={handleLike}
                style={{ color: "rgb(245, 65, 65)" }}
              />
            )}
            <BiCommentDetail />
          </div>
          <div className="like-counter">{likes} likes</div>
          <div className="post-description">
            <span className="post-id">{postInfo.user}</span>{" "}
            <span>{postInfo.description}</span>
          </div>
          <div className="comment-section">
            View all {postInfo.comments.length} comments
          </div>
        </div>
      </div>
    </>
  );
};

export default Post;
