import { useRef } from "react";
import "./MenuCSS.css";

const Menu = ({ setMenu }) => {
  const menu = useRef();
  const itemContainer =  useRef()
  return (
    <div
      className="menu-container"
      ref={menu}
      onClick={(e) => {
        if (e.target === menu.current) {
          setMenu(false)
          itemContainer.current.classlist.toggle('open')

        };
      }}
    >
      Options
      <menu ref={itemContainer}>
        <div className="menu-item">Edit Post</div>
        <div className="menu-item red">Delete Post</div>
      </menu>
    </div>
  );
};

export default Menu;
