import { useRef } from "react";
import "./MenuCSS.css";

const Menu = ({ setMenu, container }) => {
  const menu = useRef();
  return (
    <div
      className="menu-container"
      ref={menu}
      onClick={(e) => {
        if (e.target === menu.current) {
          setMenu(false)
          container.current.classList.toggle("open")
          container.current.style.top = `none`
        };
      }}
    >
      <menu>
        <div className="menu-item">Edit Post</div>
        <div className="menu-item red">Delete Post</div>
      </menu>
    </div>
  );
};

export default Menu;
