import axios from "axios";
import { useState } from "react";
import { useCallback } from "react";
import { useRef } from "react";
import "./MenuCSS.css";

const Menu = ({ setMenu, postID, user, setDescription, setError }) => {
  const menu = useRef();
  const [edit, setEdit] = useState(false);
  const [editValue, setEditValue] = useState("");
  const [deletePost, setDeletePost] = useState(false);

  const handleEdit = useCallback(async () => {
    const token = user && (await user.getIdToken());
    const headers = token ? { authtoken: token } : {};

    try {
      console.log(editValue);
      const response = await axios.put(
        `/api/posts/editPost/${postID}`,
        {
          newDescription: editValue,
        },
        { headers }
      );
      setMenu(false);
      setDescription(response.data.description);
    } catch (err) {
      setMenu(false);
      setError(err.response.data.status);
    }
  }, [user, postID, editValue, setMenu, setError, setDescription]);

  const handleDelete = useCallback(async () => {
    const token = user && (await user.getIdToken());
    const headers = token ? { authtoken: token } : {};

    try {
      const response = await axios.delete(
        `/api/posts/deletePost/${postID}`, null,
        { headers }
      );
      setMenu(false);
    } catch (err) {
      setMenu(false);
      setError(err.response.data.status);
    }
  }, [user, postID, setMenu, setError]);

  if (edit) {
    return (
      <div
        className="menu-container horizontal"
        ref={menu}
        onClick={(e) => {
          if (e.target === menu.current) {
            setMenu(false);
          }
        }}
      >
        <menu>
          <div className="menu-title">
            What would you like to change the description too?
          </div>
          <textarea
            value={editValue}
            onChange={(e) => setEditValue(e.target.value)}
            cols="30"
          />
          <div className="horizontal">
            <div className="menu-item red" onClick={() => setMenu(false)}>
              Cancel
            </div>
            <div className="menu-item" onClick={handleEdit}>
              Edit
            </div>
          </div>
        </menu>
      </div>
    );
  }

  if (deletePost) {
    return (
      <div
        className="menu-container horizontal"
        ref={menu}
        onClick={(e) => {
          if (e.target === menu.current) {
            setMenu(false);
          }
        }}
      >
        <menu>
          <div className="menu-title">
            Are you sure you want to delete this post?
          </div>
          <div className="horizontal">
            <div className="menu-item" onClick={() => setMenu(false)}>
              Cancel
            </div>
            <div className="menu-item red">Delete</div>
          </div>
        </menu>
      </div>
    );
  }

  return (
    <div
      className="menu-container"
      ref={menu}
      onClick={(e) => {
        if (e.target === menu.current) {
          setMenu(false);
        }
      }}
    >
      <menu>
        <div className="menu-item" onClick={() => setEdit(true)}>
          Edit Post
        </div>
        <div className="menu-item red" onClick={() => setDeletePost(true)}>
          Delete Post
        </div>
      </menu>
    </div>
  );
};

export default Menu;
