import { BsThreeDots, BsHeart } from "react-icons/bs";
import { BiCommentDetail } from "react-icons/bi";
import { useState } from "react";
import useUser from "../../utils/useUser";
import "./CreateNewPostCSS.css";
import axios from "axios";
import { useRef } from "react";

const CreateNewPost = ( {post}) => {
  const [imgURL, setImgURL] = useState("");
  const [description, setDescription] = useState("");
  const { user, userInfo } = useUser();
  const container = useRef()

  const handlePost = async () => {
    const token = user && (await user.getIdToken());
    const headers = token ? { authtoken: token } : {};
    const userName = userInfo.user
    const response = axios.post(
      "/api/posts/createNewPost",
      {
        user: userName,
        imgURL,
        description,
      },
      { headers }
    );
  };

  return (
    <div className="create-new-post-container" ref={container} onClick={(e) => {
        if (e.target === container.current) post.setCreatePost(!post.createPost)
    }}>
      {/* {error && (
        <div className="post-error">
          <div>{error}</div>
          <div className="x" onClick={() => setError("")}>
            &#x2715;
          </div>
        </div>
      )} */}

      <div className="post create">
        <div className="header-section">
          {userInfo.user} <BsThreeDots />{" "}
        </div>
        <div className="create-img-section">
          <input
            type="text"
            placeholder="Enter A IMG URL"
            value={imgURL}
            onChange={(e) => setImgURL(e.target.value)}
          />
        </div>
        <div className="like-comment-description-section">
          <div className="like-comment">
            <BsHeart />
            <BiCommentDetail />
          </div>
          <div className="like-counter">0 likes</div>
          <div className="post-description">
            <span className="post-id">rick</span>{" "}
          </div>
          <textarea
            className="create-description"
            placeholder="Enter a post description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <div className="comment-section">View all 0 comments</div>
          <button className="post-btn" onClick={handlePost}>Make New Post</button>
        </div>
      </div>
    </div>
  );
};

export default CreateNewPost;
