import { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import useUser from "../../hooks/useUser";
import axios from "axios";
import { BsHeartFill, BsHeart, BsThreeDots } from "react-icons/bs";
import { BiCommentDetail } from "react-icons/bi";
import "./PostCSS.css";

const Post = ({ id, img, likes, comments, description }) => {
  const [liked, setLiked] = useState(false);
  const { user, isLoading } = useUser();
  const navigate = useNavigate();

  const getPostInfo = useCallback(
    (id) => {
      const getInfo = async () => {
        const token = user && (await user.getIdToken());
        const headers = token ? { authtoken: token } : {};
        const response = await axios.get(`/api/posts/liked/${id}`, null, {
          headers,
        });
      };

      if (!isLoading) {
        getInfo();
      } else {
        return false;
      }
    },
    [user, isLoading]
  );

  const handleLike = useCallback(
    (id) => {
      const likePost = async () => {
        const token = user && (await user.getIdToken());
        const headers = token ? { authtoken: token } : {};
        const response = await axios.put(`/api/posts/like-unlike/${id}`, null, {
          headers,
        });
      };

      if (!isLoading) {
        likePost();
      } else {
        return navigate("/login");
      }
    },
    [user, isLoading]
  );

  return (
    <>
      {postData.map((post) => {
        return (
          <div className="post" key={post._id}>
            <div className="header-section">
              {post.user} <BsThreeDots />{" "}
            </div>
            <div className="img-section">
              <img src={post.imgURL} alt="Post Img" />
            </div>
            <div className="like-comment-description-section">
              <div className="like-comment">
                {!getPostInfo(post._id) ? (
                  <BsHeart onClick={() => handleLike(post._id)} />
                ) : (
                  <BsHeartFill
                    onClick={() => handleLike(post._id)}
                    style={{ color: "rgb(245, 65, 65)" }}
                  />
                )}
                <BiCommentDetail />
              </div>
              <div className="like-counter">{post.likedby.length} likes</div>
              <div className="post-description">
                <span className="post-id">{post.user}</span>{" "}
                <span>{post.description}</span>
              </div>
              <div className="comment-section">
                View all {post.comments.length} comments
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
};

export default Post;
