const CreateNewPost = () => {
  return (
    <div className="create-new-post-container">
      {error && (
        <div className="post-error">
          <div>{error}</div>
          <div className="x" onClick={() => setError("")}>
            &#x2715;
          </div>
        </div>
      )}

      <div className="post">
        <div className="header-section">
          {postInfo.user}{" "}
          <BsThreeDots
            onClick={() => {
              setMenu(true);
            }}
          />{" "}
        </div>
        <div className="img-section">
          <img src={postInfo.imgURL} alt="Post Img" />
        </div>
        <div className="like-comment-description-section">
          <div className="like-comment">
            <BsHeart
              className="animate__animated animate__bounceIn"
              onClick={handleLike}
            />

            <BiCommentDetail
              onClick={() => comments.setCommentView(!comments.commentView)}
            />
          </div>
          <div className="like-counter">{likes.likes} likes</div>
          <div className="post-description">
            <span className="post-id">{postInfo.user}</span>{" "}
            <span>{description}</span>
          </div>
          <div className="comment-section" onClick={() => setCommentView(true)}>
            View all {comments.commentsCount} comments
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateNewPost;
